package learning;

public class forLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5;
		int j=0;
		for ( i = 5; i >= 1; i--) {
			  System.out.println("i:"+i);
			  for ( j = 1; j <= i; j++) {
				  System.out.println(" j:"+j);
				  
				} 
			}
		System.out.println("i:"+i);
	}

}
